$(function () {
    $('.header__inner-burger').on('click', function () {
        $('.header__inner-list').slideToggle();
    });

    function scrollTo(element) {
        window.scroll({
            left: 0,
            top: element.offsetTop,
            behavior: 'smooth'
        })
    }
    var button = document.querySelector('.header__button-btn');
    var agency = document.querySelector('#footer');
    button.addEventListener('click', () => {
        scrollTo(agency);
    })
});
